mapper.select:
select pj_sel_id, curr_pj_like_cnt,sub_ctg,pj_id,pj_sel_name,pj_url,pj_long_title,pj_intro,pj_status,fund_result_status,
       fund_goal_money,curr_fund_money,fund_str_dtm,fund_end_dtm
from PJ
where pj_id = #{pj_id}
and pj_status = #{pj_status}

mapper.update:
update PJ
set curr_pj_like_cnt = #{curr_pj_like_cnt} + 1
where pj_id = #{pj_id}

    select curr_pj_like_cnt from PJ where pj_id = #{pj_id}

update PJ
set curr_pj_like_cnt = #{curr_pj_like_cnt} -1
where pj_id = #{pj_id}

    select curr_pj_like_cnt from PJ where pj_id = #{pj_id}

update PJ
set pj_status = #{pj_status}
where pj_id = #{pj_id}
