mapper.select:
select * from LIKE_PJ_TMP
where user_id = #{user_id}
and pj_id = #{pj_id}

select * from LIKE_PJ_TMP
where user_id = #{user_id}
and like_status = 1

mapper.update:
update LIKE_PJ_TMP
set like_status = 0
where user_id = #{user_id}
and pj_id = #{pj_id}

update LIKE_PJ_TMP
set like_status = 1
where user_id = #{user_id}
and pj_id = #{pj_id}
